import React, {useEffect, useState} from 'react';
import axios from 'axios';
import Home from './pages/Home';
import CreateCause from './pages/CreateCause';
import Profile from './pages/Profile';
import Glassdoor from './pages/Glassdoor';
import Admin from './pages/Admin';
import CauseDetail from './pages/CauseDetail';


function App(){
  const [page, setPage] = useState('home');
  const [user, setUser] = useState(null);
  useEffect(()=>{
    const raw = localStorage.getItem('dn_user');
    if(raw) setUser(JSON.parse(raw));
  },[]);

  const logout = ()=>{ localStorage.removeItem('dn_token'); localStorage.removeItem('dn_user'); setUser(null); setPage('home'); }

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow p-4 flex justify-between">
        <div className="flex items-center gap-6">
          <div className="text-xl font-bold text-indigo-700 cursor-pointer" onClick={()=>setPage('home')}>DonateNow</div>
          <button onClick={()=>setPage('create')} className="text-sm">Create Cause</button>
          <button onClick={()=>setPage('glassdoor')} className="text-sm">Glassdoor</button>
          <button onClick={()=>setPage('stories')} className="text-sm">Success Stories</button>
        </div>
        <div className="flex items-center gap-4">
          {user ? (
            <>
              <div className="flex items-center gap-2 cursor-pointer" onClick={()=>setPage('profile')}>
                <div className="w-8 h-8 rounded-full bg-indigo-200 flex items-center justify-center">{user.name[0]}</div>
                <div className="text-sm">{user.name}</div>
              </div>
              <button onClick={logout} className="text-sm bg-red-500 text-white px-3 py-1 rounded">Logout</button>
            </>
          ) : (
            <Auth setUser={setUser} />
          )}
        </div>
      </nav>
      <main className="container mx-auto p-6">
  {page==='home' && <Home user={user} onOpenCause={(id)=>{ setSelectedCauseId(id); setPage('cause'); }} />}
  {page==='create' && <CreateCause user={user} onCreated={()=>setPage('home')} />}
  {page==='profile' && <Profile user={user} />}
  {page==='glassdoor' && <Glassdoor />}
  {page==='admin' && <Admin user={user} />}
  {page==='cause' && <CauseDetail causeId={selectedCauseId} onBack={()=>setPage('home')} />}
     </main>

    </div>
  );
}

function Auth({setUser}){
  const [mode,setMode]=useState('login');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [name,setName]=useState('');
  async function submit(e){
    e.preventDefault();
    try{
      if(mode==='login'){
        const r = await axios.post('http://localhost:4000/api/login',{email,password});
        localStorage.setItem('dn_token', r.data.token);
        localStorage.setItem('dn_user', JSON.stringify(r.data.user));
        setUser(r.data.user);
      } else {
        const r = await axios.post('http://localhost:4000/api/register',{name,email,password});
        localStorage.setItem('dn_token', r.data.token);
        localStorage.setItem('dn_user', JSON.stringify(r.data.user));
        setUser(r.data.user);
      }
    }catch(err){ alert(err?.response?.data?.error || 'Error'); }
  }
  return (
    <form onSubmit={submit} className="flex items-center gap-2">
      {mode==='register' && <input required placeholder="Name" value={name} onChange={e=>setName(e.target.value)} className="border px-2 py-1 rounded" />}
      <input required placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} className="border px-2 py-1 rounded" />
      <input required placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} className="border px-2 py-1 rounded" />
      <button className="bg-indigo-600 text-white px-3 py-1 rounded" type="submit">{mode==='login'?'Login':'Register'}</button>
      <button type="button" onClick={()=>setMode(mode==='login'?'register':'login')} className="text-sm">{mode==='login'?'Sign up':'Sign in'}</button>
    </form>
  );
}

export default App;
