// frontend/src/pages/Insights.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Tooltip, Legend, Title } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend, Title);

export default function Insights(){
  const [summary, setSummary] = useState([]);
  const [topDonors, setTopDonors] = useState([]);

  useEffect(()=>{ load(); }, []);

  async function load(){
    const s = await axios.get('http://localhost:4000/api/analytics/summary');
    setSummary(s.data || []);
    // load top donors naively (client-side)
    const causes = await axios.get('http://localhost:4000/api/causes');
    const map = {};
    for(const c of causes.data){
      const tx = await axios.get(`http://localhost:4000/api/causes/${c.id}/transactions`);
      tx.data.forEach(t => {
        const name = t.donor_name || t.donor_user || 'Anonymous';
        map[name] = (map[name] || 0) + (t.amount || 0);
      });
    }
    const arr = Object.entries(map).map(([name, total])=>({name, total})).sort((a,b)=>b.total-a.total).slice(0,10);
    setTopDonors(arr);
  }

  const labels = summary.map(s=>s.title);
  const data = { labels, datasets: [{ label: 'Donations (INR)', data: summary.map(s=>s.total||0), backgroundColor: 'rgba(59,130,246,0.8)' }] };

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Impact Insights</h2>
      <div className="bg-white p-4 rounded shadow mb-4">
        <Bar data={data} options={{plugins:{title:{display:true,text:'Donations per Cause'}}}} />
      </div>
      <div className="grid md:grid-cols-2 gap-4">
        <div className="bg-white p-4 rounded shadow">
          <h4 className="font-semibold mb-2">Top Donors</h4>
          {topDonors.length===0 ? <p className="text-gray-500">No data yet</p> :
            <ol className="list-decimal pl-5">
              {topDonors.map((d, i)=> <li key={i} className="mb-1">{d.name} — ₹{Math.round(d.total)}</li>)}
            </ol>}
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h4 className="font-semibold mb-2">Suggestions</h4>
          <ul className="list-disc pl-5 text-sm text-gray-600">
            <li>Add filters by timeframe and cause categories</li>
            <li>Export CSV per cause</li>
            <li>Map-based visualization of donors (future)</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
