import React, {useState} from 'react';
import axios from 'axios';

export default function CreateCause({user,onCreated}){
  const [title,setTitle]=useState(''); const [description,setDescription]=useState(''); const [goal,setGoal]=useState(''); const [end,setEnd]=useState(''); const [photo,setPhoto]=useState(null);
  async function submit(e){
    e.preventDefault();
    const token = localStorage.getItem('dn_token');
    if(!token){ alert('Login first'); return; }
    const fd = new FormData();
    fd.append('title', title);
    fd.append('description', description);
    fd.append('goal_amount', goal);
    fd.append('end_date', end);
    if(photo) fd.append('photo', photo);
    try{
      await axios.post('http://localhost:4000/api/causes', fd, { headers: { Authorization: 'Bearer '+token, 'Content-Type':'multipart/form-data' } });
      alert('Created');
      onCreated && onCreated();
    }catch(err){ alert(err?.response?.data?.error || 'Error'); }
  }
  return (
    <div className="max-w-xl bg-white p-6 rounded shadow">
      <h3 className="text-lg font-semibold mb-4">Create Cause</h3>
      <form onSubmit={submit} className="space-y-3">
        <input required placeholder="Title" value={title} onChange={e=>setTitle(e.target.value)} className="w-full border px-3 py-2 rounded" />
        <textarea placeholder="Description" value={description} onChange={e=>setDescription(e.target.value)} className="w-full border px-3 py-2 rounded"></textarea>
        <input required placeholder="Goal Amount" type="number" value={goal} onChange={e=>setGoal(e.target.value)} className="w-full border px-3 py-2 rounded" />
        <input placeholder="End Date (YYYY-MM-DD)" value={end} onChange={e=>setEnd(e.target.value)} className="w-full border px-3 py-2 rounded" />
        <input type="file" onChange={e=>setPhoto(e.target.files[0])} />
        <div><button className="bg-indigo-600 text-white px-4 py-2 rounded" type="submit">Create Cause</button></div>
      </form>
    </div>
  );
  return (
    <div style={{display:'flex', justifyContent:'center', alignItems:'flex-start'}}>
      <div style={{width:900, maxWidth:'95%'}} className="bg-white p-6 rounded-xl shadow">
        {/* Your existing create form here */}
      </div>
    </div>
  );

}
