import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function Home({user}){
  const [causes,setCauses]=useState([]);
  useEffect(()=>{ fetchCauses(); },[]);
  async function fetchCauses(){ const r = await axios.get('http://localhost:4000/api/causes'); setCauses(r.data); }
  async function like(id){
    const token = localStorage.getItem('dn_token');
    if(!token){ alert('Login first'); return; }
    await axios.post(`http://localhost:4000/api/causes/${id}/like`, {}, { headers: { Authorization: 'Bearer '+token } });
    fetchCauses();
  }
  async function donate(id){
    const token = localStorage.getItem('dn_token');
    if(!token){ alert('Login first'); return; }
    const amt = prompt('Enter amount to donate (INR)');
    if(!amt) return;
    await axios.post(`http://localhost:4000/api/causes/${id}/donate`, {amount:amt}, { headers: { Authorization: 'Bearer '+token } });
    fetchCauses();
    alert('Thank you! Donation recorded.');
  }
  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Active Causes</h2>
      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {causes.map(c=>(
          <div key={c.id} className="bg-white p-4 rounded shadow hover:shadow-lg transition cursor-pointer"
            onClick={() => onOpenCause && onOpenCause(c.id)}>
            {c.photo && <img src={'http://localhost:4000'+c.photo} className="h-40 w-full object-cover rounded" alt="" />}
            <h3 className="mt-2 font-semibold">{c.title}</h3>
            <p className="text-sm text-gray-600">{c.description?.slice(0,120)}</p>
            <div className="mt-2 flex items-center justify-between">
              <div className="text-sm text-gray-500">₹{Math.round(c.raised_amount)} / ₹{Math.round(c.goal_amount)}</div>
              <div className="flex gap-2">
                <button onClick={()=>like(c.id)} className="px-2 py-1 border rounded">Like</button>
                <button onClick={()=>donate(c.id)} className="px-2 py-1 bg-green-600 text-white rounded">Donate</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
