// frontend/src/components/Navbar.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Navbar({ user, onNavigate }){
  const [me, setMe] = useState(user || null);

  useEffect(()=> {
    if(user) {
      setMe(user);
      return;
    }

    // Prefer client-side cached user (fast) then fallback to /api/me if token exists
    try{
      const s = localStorage.getItem('dn_user');
      if(s){ setMe(JSON.parse(s)); return; }
    }catch(e){}

    const token = localStorage.getItem('dn_token');
    if(token){
      axios.get('http://localhost:4000/api/me', { headers: { Authorization: 'Bearer '+token } })
        .then(res=> setMe(res.data.user || res.data))
        .catch(()=> setMe(null));
    }
  }, [user]);

  function firstWord(name){
    if(!name) return '';
    return name.split(' ')[0];
  }

  function handleLogout(){
    localStorage.removeItem('dn_user');
    localStorage.removeItem('dn_token');
    setMe(null);
    if(onNavigate) onNavigate('home');
  }

  function handleNav(page){
    if(onNavigate) onNavigate(page);
  }

  return (
    <nav className="bg-white p-3 shadow-sm sticky top-0 z-40">
      <div className="container mx-auto flex items-center justify-between">
        <div className="flex items-center gap-3">
          <img src="/logo192.png" alt="logo" style={{width:36, height:36}} />
          <a onClick={()=>handleNav('home')} style={{cursor:'pointer'}} className="text-2xl font-bold text-indigo-700">DonateNow</a>
          <span className="ml-3 text-sm text-gray-600">Inspire • Act • Transform</span>
        </div>

        <div className="flex items-center gap-3 text-sm">
          <a onClick={()=>handleNav('home')} style={{cursor:'pointer'}} className="px-2">Home</a>
          <a onClick={()=>handleNav('create')} style={{cursor:'pointer'}} className="px-2">Create Cause</a>
          <a onClick={()=>handleNav('stories')} style={{cursor:'pointer'}} className="px-2">Success Stories</a>
          <a onClick={()=>handleNav('insights')} style={{cursor:'pointer'}} className="px-2">Insights</a>
          {me && me.role === 'admin' ? <a onClick={()=>handleNav('admin')} style={{cursor:'pointer'}} className="px-2">Admin</a> : null}

          {me ? (
            <div className="flex items-center gap-3">
              <a onClick={()=>handleNav('profile')} style={{cursor:'pointer'}} className="flex items-center gap-3 px-2" title={me.name}>
                <div className="avatar-circle" aria-hidden>{firstWord(me.name)}</div>
                <span className="hidden sm:inline">{me.name}</span>
              </a>
              <button onClick={handleLogout} className="px-3 py-1 border rounded text-xs">Logout</button>
            </div>
          ) : (
            <a onClick={()=>handleNav('auth')} style={{cursor:'pointer'}} className="px-3 py-1 bg-indigo-600 text-white rounded">Login / Register</a>
          )}
        </div>
      </div>
    </nav>
  );
}
