// frontend/src/pages/CauseDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function CauseDetail({ causeId, onBack }) {
  const [data, setData] = useState(null);
  const [name, setName] = useState('');
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');
  const [busy, setBusy] = useState(false);

  useEffect(() => {
    if (!causeId) return;
    axios.get(`http://localhost:4000/api/causes/${causeId}`).then(r => setData(r.data));
  }, [causeId]);

  async function submitDonation(e) {
    e.preventDefault();
    const token = localStorage.getItem('dn_token');
    if (!amount || isNaN(Number(amount)) || Number(amount) <= 0) return alert('Enter a valid amount');
    setBusy(true);
    try {
      const headers = token ? { Authorization: 'Bearer ' + token } : {};
      const body = { amount: Number(amount), name: name || 'Anonymous', message };
      const res = await axios.post(`http://localhost:4000/api/causes/${causeId}/donate`, body, { headers });
      if (res.data && res.data.ok) {
        alert('Thank you for donating!');
        // if goal reached, notify user
        if (res.data.goalReached) {
          alert('Goal reached! A success story was created.');
        }
        // refresh cause data
        const r = await axios.get(`http://localhost:4000/api/causes/${causeId}`);
        setData(r.data);
      } else {
        alert('Donation failed.');
      }
    } catch (err) {
      alert(err?.response?.data?.error || 'Error donating');
    } finally {
      setBusy(false);
    }
  }

  if (!data) return <div>Loading cause...</div>;
  const { cause, donations } = data;
  const raised = Number(cause.raised_amount || 0);
  const goal = Number(cause.goal_amount || 0);
  const percent = goal > 0 ? Math.min(100, Math.round((raised / goal) * 100)) : 0;

  return (
    <div>
      <button className="mb-4 text-sm text-indigo-600" onClick={onBack}>← Back</button>

      <div className="bg-white rounded-lg p-6 shadow" data-aos="fade-up">
        <h2 className="text-2xl font-bold">{cause.title}</h2>
        <p className="text-gray-600 mt-2">{cause.description}</p>

        <div className="flex justify-between text-center mt-4">
          <div>
            <div className="text-xl font-semibold">₹{Math.round(raised)}</div>
            <div className="text-sm text-gray-500">Raised</div>
          </div>
          <div>
            <div className="text-xl font-semibold text-green-600">₹{Math.round(goal)}</div>
            <div className="text-sm text-gray-500">Goal</div>
          </div>
          <div>
            <div className="text-xl font-semibold">{donations.length}</div>
            <div className="text-sm text-gray-500">Supporters</div>
          </div>
        </div>

        <div className="progress my-3" style={{height: 25, borderRadius: 15}}>
          <div className="bg-gradient-to-r from-green-400 to-teal-500" style={{
            width: `${percent}%`,
            height: '100%',
            borderRadius: 15,
            transition: 'width 1.5s ease-in-out',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 600
          }}>
            {percent}% Funded
          </div>
        </div>

        {percent >= 100 ? (
          <div className="bg-green-100 text-green-800 p-3 rounded text-center">
            ✅ Goal Reached! Thank you to all supporters!
          </div>
        ) : (
          <p className="text-center text-gray-500">₹{Math.round(goal - raised)} more needed to reach the goal</p>
        )}
      </div>

      <div className="bg-white rounded-lg p-6 shadow mt-4" data-aos="fade-up">
        <h4 className="text-lg font-semibold text-center mb-4">Make a Difference Today</h4>

        <form onSubmit={submitDonation} className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <div>
            <label className="block text-sm">Your Name</label>
            <div className="flex items-center gap-2">
              <input name="name" value={name} onChange={e=>setName(e.target.value)} placeholder="Enter your name or 'Anonymous'" className="w-full border px-3 py-2 rounded" />
            </div>
          </div>

          <div>
            <label className="block text-sm">Donation Amount (INR)</label>
            <div className="flex items-center gap-2">
              <span className="px-2">₹</span>
              <input name="amount" value={amount} onChange={e=>setAmount(e.target.value)} type="number" min="1" className="w-full border px-3 py-2 rounded" required />
            </div>
          </div>

          <div className="md:col-span-2">
            <label className="block text-sm">Message (Optional)</label>
            <textarea name="message" value={message} onChange={e=>setMessage(e.target.value)} className="w-full border px-3 py-2 rounded" rows="2" placeholder="Leave a message of support..."></textarea>
          </div>

          <div className="md:col-span-2 text-center">
            <button type="submit" disabled={busy} className="bg-indigo-600 text-white px-6 py-2 rounded">
              <i className="fas fa-heart mr-2"></i> {busy ? 'Processing...' : 'Donate Now'}
            </button>
          </div>
        </form>

        <div className="text-center mt-4">
          <div className="text-sm text-gray-500">Quick Donation Options</div>
          <div className="flex gap-2 justify-center mt-2">
            <button className="px-3 py-1 border rounded" onClick={()=>setAmount(500)}>₹500</button>
            <button className="px-3 py-1 border rounded" onClick={()=>setAmount(1000)}>₹1000</button>
            <button className="px-3 py-1 border rounded" onClick={()=>setAmount(2000)}>₹2000</button>
          </div>
        </div>
      </div>

      <div className="grid md:grid-cols-3 gap-4 mt-4">
        <div className="md:col-span-2 bg-white p-4 rounded shadow">
          <h5 className="font-semibold">Recent Donations</h5>
          {donations.length === 0 ? <p>No donations yet — be the first!</p> : (
            <ul className="mt-2 space-y-2">
              {donations.map((d, i) => (
                <li key={i} className="border rounded p-2">
                  <div className="flex justify-between"><strong>{d.donor_name || 'User'}</strong><small className="text-gray-500">{new Date(d.timestamp).toLocaleString()}</small></div>
                  <div>₹{Math.round(d.amount)} {d.message ? <span className="text-sm text-gray-600">— {d.message}</span> : null}</div>
                </li>
              ))}
            </ul>
          )}
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h6 className="font-semibold">Share this cause</h6>
          <p className="text-sm text-gray-500">Use the browser share or copy the URL to share.</p>
          <button className="btn border px-3 py-1" onClick={()=>{ navigator.clipboard.writeText(window.location.href); alert('Link copied'); }}>Copy Link</button>
        </div>
      </div>
    </div>
  );
}
