import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function Profile({user}){
  const [data,setData]=useState(null);
  useEffect(()=>{ fetch(); },[]);
  async function fetch(){
    const token = localStorage.getItem('dn_token');
    if(!token) return;
    const r = await axios.get('http://localhost:4000/api/me', { headers: { Authorization: 'Bearer '+token } });
    setData(r.data);
  }
  if(!user) return <div>Please login to view profile.</div>;
  return (
    <div className="grid grid-cols-2 gap-6">
      <div className="bg-white p-4 rounded shadow">
        <h3 className="font-semibold">Profile</h3>
        <div className="mt-2">Name: {user.name}</div>
        <div>Email: {user.email}</div>
      </div>
      <div className="bg-white p-4 rounded shadow">
        <h3 className="font-semibold">History</h3>
        <div className="mt-2">
          <h4 className="font-semibold">Donations</h4>
          {data?.donations?.map(d=>(<div key={d.id}>{d.cause_title} — ₹{d.amount} — {d.timestamp}</div>))}
        </div>
        <div className="mt-3">
          <h4 className="font-semibold">Created Causes</h4>
          {data?.created?.map(c=>(<div key={c.id}>{c.title} — ₹{Math.round(c.raised_amount)}/{Math.round(c.goal_amount)}</div>))}
        </div>
      </div>
    </div>
  );
}
