// frontend/src/App.js
import React, { useEffect, useState } from 'react';
import './index.css';
import AOS from 'aos';
import 'aos/dist/aos.css';
import Home from './pages/Home';
import AuthPage from './pages/AuthPage';
import CauseDetail from './pages/CauseDetail';
import SuccessStories from './pages/SuccessStories';
import CreateCause from './pages/CreateCause';
import Insights from './pages/Insights';
import AdminPanel from './pages/AdminPanel';
import axios from 'axios';

function App(){
  const [page, setPage] = useState('home'); // home, auth, cause, create, stories, insights, admin
  const [selectedCauseId, setSelectedCauseId] = useState(null);
  const [user, setUser] = useState(() => {
    try { return JSON.parse(localStorage.getItem('dn_user')); } catch(e){ return null; }
  });

  useEffect(()=> {
    AOS.init({ duration: 700, once: true, mirror: false });
  }, []);

  useEffect(()=> {
    if(user) localStorage.setItem('dn_user', JSON.stringify(user));
    else localStorage.removeItem('dn_user');
  }, [user]);

  function handleAuthSuccess(u){
    setUser(u);
    setPage('home');
  }

  // top navbar (simple)
  return (
    <div style={{minHeight:'100vh', background: 'linear-gradient(180deg,#f8fafc,#fff)'}}>
      <nav className="w-full bg-white/70 backdrop-blur sticky top-0 z-40" style={{borderBottom:'1px solid #eef2f7'}}>
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div style={{display:'flex',alignItems:'center',gap:12}}>
            <div style={{fontWeight:800, color:'#4f46e5'}}>DonateNow</div>
            <div className="text-sm text-gray-600">Inspire • Act • Transform</div>
          </div>
          <div className="flex items-center gap-3">
            <button onClick={()=>setPage('home')} className="text-sm">Home</button>
            <button onClick={()=>setPage('create')} className="text-sm">Create Cause</button>
            <button onClick={()=>setPage('stories')} className="text-sm">Success Stories</button>
            <button onClick={()=>setPage('insights')} className="text-sm">Insights</button>
            <button onClick={()=>setPage('admin')} className="text-sm">Admin</button>
            {user ? (
              <div style={{display:'flex',alignItems:'center',gap:8}}>
                <div className="badge" style={{background:'#eef2ff', color:'#334155'}}>{user.name?.split(' ')[0]}</div>
                <button onClick={()=>{ setUser(null); localStorage.removeItem('dn_token'); setPage('home'); }} className="text-sm px-3 py-1 border rounded">Logout</button>
              </div>
            ) : (
              <button onClick={()=>setPage('auth')} className="text-sm px-3 py-1 bg-indigo-600 text-white rounded">Login / Register</button>
            )}
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4 py-8">
        {page === 'home' && <Home user={user} onOpenCause={(id)=>{ setSelectedCauseId(id); setPage('cause'); }} />}
        {page === 'auth' && <AuthPage onAuthSuccess={(u)=>{ handleAuthSuccess(u); }} />}
        {page === 'cause' && <CauseDetail causeId={selectedCauseId} onBack={()=>setPage('home')} user={user} />}
        {page === 'create' && <CreateCause user={user} onCreated={()=>setPage('home')} />}
        {page === 'stories' && <SuccessStories />}
        {page === 'insights' && <Insights />}
        {page === 'admin' && <AdminPanel user={user} onOpenCause={(id)=>{ setSelectedCauseId(id); setPage('cause'); }} />}
      </main>

      <footer className="py-8 text-center text-sm text-gray-500">© DonateNow — Built with ❤️</footer>

      <script>
        {`if(window && window.AOS) window.AOS && window.AOS.refresh();`}
      </script>
    </div>
  );
}

export default App;
